<template>
  <div v-show="list.length">
    <myTitle title="图层"></myTitle>
    <div class="coverage">
      <div
        v-for="item in list"
        :key="item.id"
        class="coverage-item"
        :class="isSelect(item.id) && 'active'"
        @click="select(item.id)"
      >
        <el-tooltip
          class="item"
          effect="dark"
          :content="item.name || item.type || item.text"
          placement="left"
        >
          <div class="content">
            <span v-html="iconType(item.type)" class="svg"></span>
            <span> {{ textType(item.type, item) }}</span>
          </div>
        </el-tooltip>
      </div>
    </div>
    <div class="cover">
      <el-button size="mini" :disabled="isSelectOne()" @click="down()"
        ><i class="el-icon-bottom"></i
      ></el-button>
      <el-button size="mini" :disabled="isSelectOne()" @click="up()"
        ><i class="el-icon-top"></i
      ></el-button>
      <el-button size="mini" :disabled="isSelectOne()" @click="mostDown()"
        ><i class="el-icon-download"></i
      ></el-button>
      <el-button size="mini" :disabled="isSelectOne()" @click="topUp()"
        ><i class="el-icon-upload2"></i
      ></el-button>
    </div>
  </div>
</template>

<script>
import select from "@/mixins/select";
import myTitle from "@/components/myTitle";
export default {
  components: { myTitle },
  mixins: [select],
  data() {
    return {
      list: [],
    };
  },
  mounted() {
    this.$nextTick(() => {
      // 当选择画布中的对象时，该对象不出现在顶层。
      this.canvas.c.preserveObjectStacking = true;
      // 渲染后拿到所有canvas对象
      this.canvas.c.on("after:render", this.getList);
    });
  },
  methods: {
    // 拿到canvas对象的
    getList() {
      // 拿到所有canvas对象，将后加入的放在后面
      this.list = [...this.canvas.c.getObjects()].reverse().map((item) => {
        const { type, id, name, text } = item;
        return { type, id, name, text };
      });
    },
    // 判断是否选中
    isSelect(id) {
      return id === this.mSelectId || this.mSelectIds.includes(id);
    },
    // 是否选中一个
    isSelectOne() {
      return this.mSelectMode !== "one";
    },
    // 选中图层
    select(id) {
      let selected = this.canvas.c.getObjects().find((item) => id === item.id);
      this.canvas.c.discardActiveObject();
      this.canvas.c.setActiveObject(selected);
      this.canvas.c.requestRenderAll();
    },
