    },
    textType(type, item) {
      if (type.includes("text")) {
        return item.name || item.text;
      }
      const typeText = {
        group: "组合",
        image: "图片",
        rect: "矩形",
        circle: "圆形",
        triangle: "三角形",
        path: "路径",
      };
      return typeText[type] || "默认元素";
    },
    // 向上显示一层
    up() {
      const activeObject = this.canvas.c.getActiveObject();
      activeObject && activeObject.bringForward();
      this.canvas.c.renderAll();
    },
    // 向下显示一层
    down() {
      const activeObject = this.canvas.c.getActiveObject();
      activeObject && activeObject.sendBackwards();
      this.canvas.c.renderAll();
    },
    //最上层显示
    topUp() {
      const activeObject = this.canvas.c.getActiveObject();
      activeObject && activeObject.bringToFront();
      this.canvas.c.renderAll();
    },
    // 最底层显示
    mostDown() {
      const activeObject = this.canvas.c.getActiveObject();
      activeObject && activeObject.sendToBack();
      this.canvas.c.renderAll();
    },
  },
};
</script>

<style lang="less" scoped>
.coverage {
  max-height: 150px;
  overflow: scroll;
  cursor: pointer;
  .coverage-item {
    font-size: 12px;
    height: 25px;
    background-color: #f6f6f6;
    line-height: 25px;
    margin-bottom: 5px;
    border-radius: 4px;
  }
  .active {
    background-color: skyblue;
  }
  .content {
    width: 210px;
    height: 25px;
    position: relative;
    overflow: hidden;
    span:first-child {
      display: inline-block;
      width: 30px;
      height: 18px;
      text-align: center;
      position: absolute;
      top: 3px;
      border-right: 1px solid #ccc;
    }
    span:last-child {
      position: absolute;
      left: 45px;
    }
  }
}
</style>